import React, { useState, useEffect } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:3001');

interface SignInRecord {
  name: string;
  time: string;
  distance: number;
}

function App() {
  const [qrValue, setQrValue] = useState<string>('');
  const [distanceLimit, setDistanceLimit] = useState<number>(100);
  const [signInRecords, setSignInRecords] = useState<SignInRecord[]>([]);
  const [showSettings, setShowSettings] = useState<boolean>(false);

  useEffect(() => {
    socket.on('signIn', (data: SignInRecord) => {
      setSignInRecords(prev => [...prev, data]);
    });

    return () => {
      socket.off('signIn');
    };
  }, []);

  const generateQR = () => {
    const timestamp = Date.now();
    const data = JSON.stringify({
      timestamp,
      distanceLimit,
      expiresAt: timestamp + 3 * 60 * 60 * 1000 // 3小时后过期
    });
    setQrValue(data);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>签到系统</h1>
        <button onClick={() => setShowSettings(true)}>生成签到二维码</button>
        
        {qrValue && (
          <div className="qr-container">
            <QRCodeSVG value={qrValue} size={256} />
            <p>二维码将在3小时后过期</p>
          </div>
        )}

        {showSettings && (
          <div className="settings-modal">
            <h2>设置签到参数</h2>
            <div>
              <label>距离限制（米）：</label>
              <input
                type="number"
                value={distanceLimit}
                onChange={(e) => setDistanceLimit(Number(e.target.value))}
              />
            </div>
            <button onClick={() => {
              generateQR();
              setShowSettings(false);
            }}>确认生成</button>
            <button onClick={() => setShowSettings(false)}>取消</button>
          </div>
        )}

        <div className="records">
          <h2>签到记录</h2>
          <table>
            <thead>
              <tr>
                <th>姓名</th>
                <th>时间</th>
                <th>距离（米）</th>
              </tr>
            </thead>
            <tbody>
              {signInRecords.map((record, index) => (
                <tr key={index}>
                  <td>{record.name}</td>
                  <td>{record.time}</td>
                  <td>{record.distance}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </header>
    </div>
  );
}

export default App;
